import { useState } from "react";
import art from './images/art.png';
import biology from './images/biology.png';
import drama from './images/drama.png';
import english from './images/english.png';
import geography from './images/geography.png';
import history from './images/history.png';
import it from './images/it.png';
import maths from './images/maths.png';
import music from './images/music.png';
import science from './images/science.png';
import swimming from './images/swimming.png';
import pe from './images/pe.png';

const Subjects = () => {

    const [weekDay, setWeekDay] = useState('Tuesday');

    const [subjects, setSubjects] = useState([
        {
            id: 1,
            name: 'Art',
            day: 'Monday',
            image: art
        },
        {
            id: 2,
            name: 'English',
            day: 'Tuesday',
            image: english
        },
        {
            id: 3,
            name: 'Maths',
            day:  'Wednesday',
            image: maths
        },
        {
            id: 4,
            name: 'Music',
            day: 'Wednesday',
            image: music
        },
        {
            id: 5,
            name: 'Biology',
            day: 'Friday',
            image: biology
        },
        {
            id: 6,
            name: 'Geography',
            day: 'Thursday',
            image: geography
        },
        {
            id: 7,
            name: 'History',
            day: 'Thursday',
            image: history
        },
        {   id: 8,
            name: 'IT',
            day: 'Monday',
            image: it
        },
        {
            id: 9,
            name: 'Science',
            day: 'Friday',
            image: science
        },
        {
            id: 10,
            name: 'Drama',
            day: 'Tuesday',
            image: drama
        },
        {
            id: 11,
            name: 'Swimming',
            day: 'Friday',
            image: swimming
        },
        {
            id: 12,
            name: 'Physical education',
            day: 'Tuesday',
            image: pe
        }
])

    function handleDayChange (event) {
        setWeekDay(event.target.value)
    }

    function handleSubjectCardClick(event) {
        // const tranformedSubjects = subjects.map((subject) => subject.id === parseInt(event.currentTarget.id)
        // ? (subject.day === weekDay)? {...subject, day:''}:{...subject, day: weekDay} : subject)
        // setSubjects(tranformedSubjects);
        console.log('click');
    }

    return (
        <main className="container">
            <div className="sisi container">
                <select className="form-select" value={weekDay} onChange={handleDayChange}>
                    <option value="Monday">Monday</option>
                    <option value="Tuesday">Tuesday</option>
                    <option value="Wednesday">Wednesday</option>
                    <option value="Thursday">Thursday</option>
                    <option value="Friday">Friday</option>
                </select>
            </div>
            <div className="list" style={{cursor: "pointer"}} onClick={handleSubjectCardClick}>
                {subjects.map((subject) => (
                    <div className={(subject.day === weekDay ? 'list_item selected' : 'list_item')}>
                        <h3>{subject.name}</h3>
                        <img src={subject.image} alt={subject.name} />  
                    </div> 
                ))
                }
            </div>
        </main>
    )
}
export default Subjects;
