import art from './images/art.png'

const Subjects = () => {

    const [subjects, setSubjects] = useState([
        {
            id: 1,
            name: 'Art',
            day: 'Monday',
            image: art
        },
        {
            id: 2,
            name: 'English',
            day: 'Tuesday'
        },
        {
            id: 3,
            name: 'Maths',
            day:  'Wednesday'
        },
        {
            id: 4,
            name: 'Music',
            day: 'Wednesday'
        },
        {
            id: 5,
            name: 'Biology',
            day: 'Friday'
        },
        {
            id: 6,
            name: 'Geography',
            day: 'Thursday'
        },
        {
            id: 7,
            name: 'History',
            day: 'Thursday'
        },
        {   id: 8,
            name: 'IT',
            day: 'Monday'
        },
        {
            id: 9,
            name: 'Science',
            day: 'Friday'
        },
        {
            id: 10,
            name: 'Drama',
            day: 'Tuesday'
        },
        {
            id: 11,
            name: 'Swimming',
            day: 'Friday'
        }

])
    return (
        <main>
            {
                subjects.map((subject) => (
                    <div>
                        <p>{subject.name}</p>
                        <img src={subject.image} alt={subject.name} />
                    </div>
                ))
            }
        </main>
    )
}

export default Subjects;
